INSERT INTO `testimoni` (`id`, `nama_pemberi`, `testimoni`, `foto`, `tanggal`) VALUES
(3, 'Faisal Malik (Mahasiswa ILKOM UGM)', '<p>Pelatihannya sangat bermanfaat. Jadi menambah pengetahuan saya tentang komputer, terutama bagian hardware. Sekarang saya sudah berani membongkar komputer sendiri.</p>\r\n', 'faisal-malik-training-teknisi-komputer-lauwba.jpg', '2019-09-25 01:42:16'),
(6, 'Grey E (Mahasiswa TI STMIK AKAKOM & Alumni D3 POLITEKNIK NEGERI AMBON)', '<p>Dengan adanya pelatihan ini, sangat membantu saya. Apalagi dengan biaya cukup murah dan juga pematerinya merupakan orang yang ahli di bidang tersebut. Dengan sistem pembelajaran full praktek, jadi lebih mudah dipahami.</p>\r\n', 'GREY-training-web-android-lauwba.jpg', '2019-09-25 01:42:25'),
(7, 'Agussalim,S.Pd  (Guru SMK Negeri Maros Sulawesi Selatan)', '<p>Alhamdulillah setelah mengikuti pelatihan komputer di LKP, sangat membantu saya mengajar di sekolah dan mendapatkan ilmu baru terutama dalam hal praktek merakit PC/laptop dan menginstal.</p>\r\n', 'agusalim-training-teknisi-komputer-lauwba2.jpg', '2019-09-25 01:42:59'),
(10, 'Zata Atika (Mahasiswa ILKOM UGM)', '<p>Dengan mengikuti pelatihan di LKP, membuat saya memiliki banyak ilmu baru yang tidak akan didapatkan di dalam kelas perkuliahan. Materi yang diajarkan walaupun banyak tapi mudah dimengerti.<br />\r\nTerimakasih LKP.</p>\r\n', 'zata-atika-training-web-android-lauwba-jogja-makassar.jpg', '2019-09-25 01:43:12');
